import 'package:flutter/material.dart';
import 'package:flutter/rendering.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:flutter_svg/flutter_svg.dart';
import 'package:herois/application/info/info_form/info_form_bloc.dart';
import 'package:herois/domain/info/value_objects.dart';

class GenderWidget extends StatelessWidget {
  const GenderWidget({
    Key key,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return BlocBuilder<InfoFormBloc, InfoFormState>(
      buildWhen: (p, c) => p.info.gender != c.info.gender,
      builder: (context, state) {
        return Container(
          height: 80,
          child: ListView.separated(
            padding: const EdgeInsets.symmetric(horizontal: 8),
            scrollDirection: Axis.horizontal,
            shrinkWrap: true,
            itemCount: Gender.predefinedGender.length,
            separatorBuilder: (context, index) {
              return Padding(
                padding: const EdgeInsets.only(
                    left: 16.0, right: 16.0, bottom: 20.0),
                child: SvgPicture.string(
                  '<svg viewBox="180.0 282.7 1.0 38.4" ><path transform="translate(-1528.0, 66.0)" d="M 1708 216.7196197509766 L 1708 255.1099548339844" fill="none" stroke="#c9c9c9" stroke-width="1" stroke-miterlimit="4" stroke-linecap="butt" /></svg>',
                  allowDrawingOutsideViewBox: true,
                ),
              );
            },
            itemBuilder: (context, index) {
              final itemGender = Gender.predefinedGender[index];
              if (index == 0) {
                return GestureDetector(
                  onTap: () {
                    context
                        .bloc<InfoFormBloc>()
                        .add(InfoFormEvent.genderChanged(itemGender));
                  },
                  child: Row(
                    crossAxisAlignment: CrossAxisAlignment.stretch,
                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                    children: <Widget>[
                      Column(
                        children: <Widget>[
                          SvgPicture.string(
                            state.info.gender.value.fold(
                              (_) => svgMale,
                              (gender) => gender == itemGender
                                  ? svgMaleClicked
                                  : svgMale,
                            ),
                            allowDrawingOutsideViewBox: true,
                          ),
                          Text(
                            "Masculino",
                            style: TextStyle(
                                color: state.info.gender.value.fold(
                                  (_) => const Color(0xffa2a2a2),
                                  (gender) => gender == itemGender
                                      ? const Color(0xffff3257)
                                      : const Color(0xffa2a2a2),
                                ),
                                fontSize: 12.0,
                                fontWeight: FontWeight.normal,
                                fontFamily: 'Montserrat'),
                          ),
                        ],
                      ),
                    ],
                  ),
                );
              } else {
                return GestureDetector(
                  onTap: () {
                    context
                        .bloc<InfoFormBloc>()
                        .add(InfoFormEvent.genderChanged(itemGender));
                  },
                  child: Row(
                    crossAxisAlignment: CrossAxisAlignment.stretch,
                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                    children: <Widget>[
                      Column(
                        children: <Widget>[
                          SvgPicture.string(
                            state.info.gender.value.fold(
                              (_) => svgFemale,
                              (gender) => gender == itemGender
                                  ? svgFemaleClicked
                                  : svgFemale,
                            ),
                            allowDrawingOutsideViewBox: true,
                          ),
                          Text(
                            "Feminino",
                            style: TextStyle(
                                color: state.info.gender.value.fold(
                                  (_) => const Color(0xffa2a2a2),
                                  (gender) => gender == itemGender
                                      ? const Color(0xffff3257)
                                      : const Color(0xffa2a2a2),
                                ),
                                fontSize: 12.0,
                                fontWeight: FontWeight.normal,
                                fontFamily: 'Montserrat'),
                          ),
                        ],
                      ),
                    ],
                  ),
                );
              }
            },
          ),
        );
      },
    );
  }
}

